#!/bin/bash

apt-get update -y
apt install -y software-properties-common
apt-add-repository --yes --update ppa:ansible/ansible
apt install -y ansible sudo
useradd -s /bin/bash -m masum
mkdir /home/masum/.ssh
cat > /home/masum/.ssh/authorized_keys <<EOF
ssh-rsa  khabir@macs-MacBook-Pro-2.local
ssh-rsa  khabir@macs-MacBook-Pro-2.local
ssh-rsa  masum@centos7.0-master
ssh-rsa  masum@ubuntu
EOF
cat > /etc/sudoers.d/10_masum <<EOF
masum ALL=(ALL) NOPASSWD: ALL
EOF
